t1.NewTopic(in:=ADR('/+/test/#'),test:= TRUE, SizeOfString:= 0);
	sp := t1.GetSplitTopic();
	pts[0] := sp^.Topics[0];
	pts[1] := sp^.Topics[1];
	pts[2] := sp^.Topics[2];
	pts[3] := sp^.Topics[3];
	pts[4] := sp^.Topics[4];
	pts[5] := sp^.Topics[5];
