t2.NewTopic(in:=ADR('/rtt/test/tr'),test:= FALSE, SizeOfString:= 0);
	sp := t2.GetSplitTopic();
	pts[10] := sp^.Topics[0];
	pts[11] := sp^.Topics[1];
	pts[12] := sp^.Topics[2];
	pts[13] := sp^.Topics[3];
